public void mouseDragged(MouseEvent e) {
    super.mouseDragged(e);
    ChartComponentBase.MouseInteraction activeInteraction = getImplementation().getActiveInteraction();
    if (activeInteraction != null) {
        activeInteraction.apply(e);
        // myUIFacade.repaint2();
        // e.consume();
        // return;
    }
}

public void mouseWheelMoved(MouseWheelEvent e) {
    if (isRotationUp(e)) {
        fireZoomOut();
    } else {
