void paintMe(Graphics g) {
    calculateCoordinates(g);
    g.setFont(GanttGraphicArea.ChartOptionsPreviewPanel.Text.FONT);
    g.drawString(text, x, y);
}

private void calculateCoordinates(Graphics g) {
    int textHeight = g.getFontMetrics(GanttGraphicArea.ChartOptionsPreviewPanel.Text.FONT).getHeight();
    int textWidth = g.getFontMetrics(GanttGraphicArea.ChartOptionsPreviewPanel.Text.FONT).stringWidth(text);
    switch (position) {
        case GanttGraphicArea.ChartOptionsPreviewPanel.Text.UP :
            y = (taskBar.y) - (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN);
            x = ((taskBar.x) + ((taskBar.width) / 2)) - (textWidth / 2);
            break;
        case GanttGraphicArea.ChartOptionsPreviewPanel.Text.DOWN :
            x = ((taskBar.x) + ((taskBar.width) / 2)) - (textWidth / 2);
            y = (((taskBar.y) + (taskBar.height)) + textHeight) - (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN);
            break;
        case GanttGraphicArea.ChartOptionsPreviewPanel.Text.LEFT :
            y = (((taskBar.y) + ((taskBar.height) / 2)) + (textHeight / 2)) - (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN);
            x = ((taskBar.x) - (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN)) - textWidth;
            break;
        case GanttGraphicArea.ChartOptionsPreviewPanel.Text.RIGHT :
            y = (((taskBar.y) + ((taskBar.height) / 2)) + (textHeight / 2)) - (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN);
            x = ((taskBar.x) + (taskBar.width)) + (GanttGraphicArea.ChartOptionsPreviewPanel.Text.MARGIN);
            break;
    }
}

