/**
 * A menu has been activate
 */
public void actionPerformed(ActionEvent evt) {
    if ((evt.getSource()) instanceof JMenuItem) {
        String arg = evt.getActionCommand();
        // /////////////////////////////////////////////////////////////////////////////////////////////////////////
        if (arg.equals(language.getText("hideTask"))) {
            tree.hideSelectedNodes();
        } else
            if (arg.equals(language.getText("displayHiddenTasks"))) {
                tree.displayHiddenTasks();
            } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                if (arg.equals(GanttProject.correctLabel(language.getText("preview")))) {
                    previewPrint();
                } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                    if (arg.equals(GanttProject.correctLabel(language.getText("deleteTask")))) {
                        deleteTasks(true);
                    } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                        if (arg.equals(GanttProject.correctLabel(language.getText("editCalendars")))) {
                            GanttDialogCalendar dialogCalendar = new GanttDialogCalendar(this);
                            dialogCalendar.show();
                        } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                            if (arg.equals(GanttProject.correctLabel(language.getText("projectCalendar")))) {
                                System.out.println("Project calendar");
                            } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                                if (arg.equals(GanttProject.correctLabel(language.getText("webPage")))) {
                                    try {
                                        openWebPage();
                                    } catch (Exception e) {
                                        System.err.println(e);
                                    }
                                } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                                    if (arg.equals(GanttProject.correctLabel(language.getText("tipsOfTheDay")))) {
                                        TipsDialog tips = new TipsDialog(this, options.getOpenTips());
                                        tips.show();
                                    } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                                        if (arg.equals(GanttProject.correctLabel(language.getText("manual")))) {
                                            if ((helpFrame) == null) {
                                                try {
                                                    helpFrame = new MainFrame("/docs/help/", "eclipse");
                                                    // Other sort of manual
                                                    String sl = language.getText("shortLanguage");
                                                    if ((sl.equals("fr")) || (sl.equals("bg")))
                                                        helpFrame = new MainFrame((("/docs/help_" + sl) + "/"), "eclipse");

                                                    helpFrame.setTitle(language.getText("ganttManual"));
                                                    ImageIcon icon = new ImageIcon(getClass().getResource("/icons/ganttproject.png"));
                                                    helpFrame.setIconImage(icon.getImage());
                                                    helpFrame.setSize(((int) ((Toolkit.getDefaultToolkit().getScreenSize().getWidth()) * 0.75)), ((int) ((Toolkit.getDefaultToolkit().getScreenSize().getHeight()) * 0.75)));
                                                    helpFrame.setLocationRelativeTo(null);
                                                } catch (Exception e) {
                                                }
                                            }
                                            helpFrame.setVisible(true);
                                        } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                                            if (arg.equals(GanttProject.correctLabel(language.getText("about")))) {
                                                aboutDialog();
                                            } else// /////////////////////////////////////////////////////////////////////////////////////////////////////////

                                                if (arg.equals(GanttProject.correctLabel(language.getText("sendMail")))) {
                                                    getTabs().setSelectedIndex(1);
                                                    getResourcePanel().sendMail(this);
                                                } else
                                                    if (arg.equals(GanttProject.correctLabel(language.getText("settings")))) {
                                                        launchOptionsDialog();
                                                    }











    } else
        if ((evt.getSource()) instanceof Document) {
            if (checkCurrentProject()) {
                final ActionEvent ae = evt;
                getUndoManager().undoableEdit("StartupDocument", new Runnable() {
                    public void run() {
                        openStartupDocument(((Document) (ae.getSource())));
                    }
                });
            }
        } else // //////////////////////////////////////////////////////////////////////////////////////////////////////////
        // Test if its buttons actions
        {
            if ((evt.getSource()) == (bImport)) {
                // import
                ImportFileWizardImpl wizard = new ImportFileWizardImpl(this.getUIFacade(), this);
                wizard.show();
            } else
                if ((evt.getSource()) == (bPreviewPrint)) {
                    // print
                    previewPrint();
                } else
                    if ((evt.getSource()) == (bShowHiddens)) {
                        showHiddenButtonsPaneMenu();
                    }


        }

    // repaint();
}

/**
 * Delete the currant task
 *
 * @param confirmation
 * 		TODO
 */
public void deleteTasks(boolean confirmation) {
    getTabs().setSelectedIndex(UIFacade.GANTT_INDEX);
    final DefaultMutableTreeNode[] cdmtn = tree.getSelectedNodes();
    if ((cdmtn == null) || ((cdmtn.length) == 0)) {
        getUIFacade().setStatusText(language.getText("msg21"));
        return;
    }
    UIFacade.Choice choice = getUIFacade().showConfirmationDialog(language.getText("msg19"), language.getText("question"));
    if (choice == (UIFacade.Choice.YES)) {
        getUndoManager().undoableEdit("Task removed", new Runnable() {
            public void run() {
                ArrayList fathers = new ArrayList();
                tree.stopEditing();
                for (int i = 0; i < (cdmtn.length); i++) {
                    if (((cdmtn[i]) != null) && ((cdmtn[i]) instanceof TaskNode)) {
                        Task ttask = ((Task) (cdmtn[i].getUserObject()));
                        getTaskManager().deleteTask(ttask);
                        ttask.delete();
                        DefaultMutableTreeNode father = tree.getFatherNode(ttask);
                        tree.removeCurrentNode(cdmtn[i]);
                        if (father != null) {
                            GanttTask taskFather = ((GanttTask) (father.getUserObject()));
                            AdjustTaskBoundsAlgorithm alg = getTaskManager().getAlgorithmCollection().getAdjustTaskBoundsAlgorithm();
                            alg.run(taskFather);
                            // taskFather.refreshDateAndAdvancement(tree);
                            father.setUserObject(taskFather);
                            fathers.add(father);
                        }
                    } else
                        if (((cdmtn[i]) != null) && ((cdmtn[i]) instanceof BlankLineNode)) {
                            ((GanttTreeTableModel) (tree.getTreeTable().getTreeTableModel())).removeNodeFromParent(cdmtn[i]);
                        }

                }
                for (int i = 0; i < (fathers.size()); i++) {
                    DefaultMutableTreeNode father = ((DefaultMutableTreeNode) (fathers.get(i)));
                    if ((father.getChildCount()) == 0)
                        ((Task) (father.getUserObject())).setProjectTask(false);

                }
            }
        });
        refreshProjectInfos();
        area.repaint();
        this.repaint2();
        getResourcePanel().area.repaint();
        setAskForSave(true);
        // setQuickSave (true);
        // quickSave("deleteTasks");
    }
}

public void open(Document document) throws IOException {
    openDocument(document);
    if ((document.getPortfolio()) != null) {
        Document defaultDocument = document.getPortfolio().getDefaultDocument();
        openDocument(defaultDocument);
    }
}

private void openDocument(Document document) throws IOException {
    if ((document.getDescription().toLowerCase().endsWith(".xml")) || (document.getDescription().toLowerCase().endsWith(".gan"))) {
        boolean locked = document.acquireLock();
        if (!locked) {
            getUIFacade().logErrorMessage(new Exception(language.getText("msg13")));
        }
        document.read();
        if (documentsMRU.add(document)) {
            updateMenuMRU();
        }
        if (locked) {
            projectDocument = document;
        }
        this.setTitle(((((language.getText("appliTitle")) + " [") + (document.getDescription())) + "]"));
        setAskForSave(false);
        if ((myPreviousStates.size()) != 0) {
            bComparePrev.setEnabled(true);
        }
    } else {
        String errorMessage = ((language.getText("msg2")) + "
") + (document.getDescription());
        throw new IOException(errorMessage);
    }
    Chart[] charts = Mediator.getPluginManager().getCharts();
    for (int i = 0; i < (charts.length); i++) {
        charts[i].setTaskManager(myTaskManager);
        charts[i].reset();
    }
    // myDelayManager.fireDelayObservation(); // it is done in repaint2
    addMouseListenerToAllContainer(this.getComponents());
    getTaskManager().processCriticalPath(((TaskNode) (tree.getRoot())));
    ArrayList projectTasks = tree.getProjectTasks();
    if ((projectTasks.size()) != 0)
        for (int i = 0; i < (projectTasks.size()); i++)
            getTaskManager().processCriticalPath(((TaskNode) (projectTasks.get(i))));


    // repaint2();
}

public void openStartupDocument(String path) {
    if (path != null) {
        final Document document = getDocumentManager().getDocument(path);
        // openStartupDocument(document);
        getUndoManager().undoableEdit("OpenFile", new Runnable() {
            public void run() {
                try {
                    getProjectUIFacade().openProject(document, getProject());
                } catch (IOException e) {
                    if (!(tryImportDocument(document))) {
                        getUIFacade().showErrorDialog(e);
                    }
                }
            }
        });
    }
}

private boolean tryImportDocument(Document document) {
    boolean success = false;
    Importer[] importers = ((Importer[]) (Mediator.getPluginManager().getExtensions(Importer.EXTENSION_POINT_ID, Importer.class)));
    for (int i = 0; i < (importers.length); i++) {
        Importer nextImporter = importers[i];
        if (Pattern.matches(((".*(" + (nextImporter.getFileNamePattern())) + ")$"), document.getFilePath())) {
            try {
                nextImporter.run(this, getUIFacade(), new File(document.getFilePath()), false);
                success = true;
                break;
            } catch (Throwable e) {
                if (!(GPLogger.log(e))) {
                    e.printStackTrace(System.err);
                }
            }
        }
    }
    return success;
}

