public void dragEnter(DropTargetDragEvent dtde) {
    if ((ghostImage) == null) {
        // In case if you drag a file from out and its not an
        // acceptable, and it can crash if the image is null
        ghostImage = new BufferedImage(1, 1, BufferedImage.TYPE_INT_ARGB_PRE);
    }
    if (!(isDragAcceptable(dtde)))
        dtde.rejectDrag();
    else
        dtde.acceptDrag(dtde.getDropAction());

}

public void dragOver(DropTargetDragEvent dtde) {
    if (!(isDragAcceptable(dtde)))
        dtde.rejectDrag();
    else
        dtde.acceptDrag(dtde.getDropAction());

    // Even if the mouse is not moving, this method is still invoked
    // 10 times per second
    Point pt = dtde.getLocation();
    if (pt.equals(lastEventPoint))
        return;

    lastEventPoint = pt;
    Graphics2D g2 = ((Graphics2D) (treetable.getGraphics()));
    // If a drag image is not supported by the platform, then draw our
    // own drag image
    if (!(DragSource.isDragImageSupported())) {
        // Rub out the last ghost image and cue line
        treetable.paintImmediately(ghostImageRect.getBounds());
        // And remember where we are about to draw the new ghost image
        ghostImageRect.setRect(((pt.x) - (offsetPoint.x)), ((pt.y) - (offsetPoint.y)), ghostImage.getWidth(), ghostImage.getHeight());
        g2.drawImage(ghostImage, AffineTransform.getTranslateInstance(ghostImageRect.getX(), ghostImageRect.getY()), null);
    } else {
        // Just rub out the last cue line
        treetable.paintImmediately(cueLineRect.getBounds());
    }
    TreePath path = treetable.getTree().getClosestPathForLocation(pt.x, pt.y);
    if (!(path == (lastPath))) {
        lastPath = path;
        hoverTimer.restart();
    }
    // In any case draw (over the ghost image if necessary) a cue line
    // indicating where a drop will occur
    Rectangle raPath = treetable.getTree().getPathBounds(path);
    if (raPath == null)
        raPath = new Rectangle(1, 1);

    cueLineRect.setRect(0, ((raPath.y) + ((int) (raPath.getHeight()))), getWidth(), 2);
    g2.setColor(cueLineColor);
    g2.fill(cueLineRect);
    // And include the cue line in the area to be rubbed out next time
    ghostImageRect = ghostImageRect.createUnion(cueLineRect);
}

public void dropActionChanged(DropTargetDragEvent dtde) {
    if (!(isDragAcceptable(dtde)))
        dtde.rejectDrag();
    else
        dtde.acceptDrag(dtde.getDropAction());

}

public void drop(DropTargetDropEvent dtde) {
    if (!(isDropAcceptable(dtde))) {
        dtde.rejectDrop();
        return;
    }
    // Prevent hover timer from doing an unwanted expandPath or
    // collapsePath
    hoverTimer.stop();
    dtde.acceptDrop(dtde.getDropAction());
    Transferable transferable = dtde.getTransferable();
    DataFlavor[] flavors = transferable.getTransferDataFlavors();
    for (int i = 0; i < (flavors.length); i++) {
        DataFlavor flavor = flavors[i];
        if (flavor.isMimeTypeEqual(DataFlavor.javaJVMLocalObjectMimeType)) {
            try {
                Point pt = dtde.getLocation();
                DefaultMutableTreeNode target = ((DefaultMutableTreeNode) (treetable.getTree().getClosestPathForLocation(pt.x, pt.y).getLastPathComponent()));
                TreePath pathSource = ((TreePath) (transferable.getTransferData(flavor)));
                DefaultMutableTreeNode source = ((DefaultMutableTreeNode) (pathSource.getLastPathComponent()));
                TreePath pathNewChild = null;
                TreeNode sourceFather = source.getParent();
                int index = sourceFather.getIndex(source);
                source.removeFromParent();
                treeModel.nodesWereRemoved(sourceFather, new int[]{ index }, new Object[]{ source });
                ((GanttTreeTableModel) (treeModel)).insertNodeInto(source, target, 0);
                pathNewChild = new TreePath(((DefaultMutableTreeNode) (pathSource.getLastPathComponent())).getPath());
                if (pathNewChild != null) {
                    treetable.getTree().setSelectionPath(pathNewChild);// Mark

                    // this
                    // as
                    // the
                    // selected
                    // path
                    // in
                    // the
                    // tree
                }
                // refreshAllFather(source.getUserObject().toString());
                expandRefresh(source);
                forwardScheduling();
                area.repaint();
                appli.setAskForSave(true);
                break;// No need to check remaining flavors

            } catch (UnsupportedFlavorException ufe) {
                System.out.println(ufe);
                dtde.dropComplete(false);
                return;
            } catch (IOException ioe) {
                System.out.println(ioe);
                dtde.dropComplete(false);
                return;
            }
        }
    }
    dtde.dropComplete(true);
}

