    }
    treetable.repaint();
}

public boolean isDragAcceptable(DropTargetDragEvent e) {
    // Only accept COPY or MOVE gestures (ie LINK is not supported)
    if (((e.getDropAction()) & (DnDConstants.ACTION_COPY_OR_MOVE)) == 0) {
        return false;
    }
    // Only accept this particular flavor
    if (!(e.isDataFlavorSupported(GanttTree2.GanttTransferableTreePath.TREEPATH_FLAVOR))) {
        return false;
    }
    // Do not accept dropping on the source node
    Point pt = e.getLocation();
    TreePath path = treetable.getTree().getClosestPathForLocation(pt.x, pt.y);
    if (dragPath.isDescendant(path)) {
        return false;
    }
    if (path.equals(dragPath)) {
        return false;
    }
    // Check if the task is a milestone task
    Task task = ((Task) (((DefaultMutableTreeNode) (path.getLastPathComponent())).getUserObject()));
    if (task.isMilestone())
        return false;

    return true;
}

public boolean isDropAcceptable(DropTargetDropEvent e) {
    // Only accept COPY or MOVE gestures (ie LINK is not supported)
    if (((e.getDropAction()) & (DnDConstants.ACTION_COPY_OR_MOVE)) == 0) {
        return false;
    }
    // Only accept this particular flavor
    if (!(e.isDataFlavorSupported(GanttTree2.GanttTransferableTreePath.TREEPATH_FLAVOR))) {
        return false;
    }
    // prohibit dropping onto the drag source
    Point pt = e.getLocation();
    TreePath path = treetable.getTree().getClosestPathForLocation(pt.x, pt.y);
    if (path.equals(dragPath)) {
        return false;
    }
    return true;
}

public boolean isDataFlavorSupported(DataFlavor flavor) {
    return Arrays.asList(_flavors).contains(flavor);
}

public synchronized Object getTransferData(DataFlavor flavor) throws UnsupportedFlavorException {
    // DataFlavor.javaJVMLocalObjectMimeType))
    if (flavor.isMimeTypeEqual(GanttTree2.GanttTransferableTreePath.TREEPATH_FLAVOR.getMimeType()))
        return _path;
    else
        throw new UnsupportedFlavorException(flavor);

}

