public void setDisplayedColumns(GanttTreeTable.DisplayedColumnsList displayedColumns) {
    GanttTreeTable.DisplayedColumnsList l = ((GanttTreeTable.DisplayedColumnsList) (displayedColumns.clone()));
    displayAllColumns();
    hideAllColumns();
    this.listDisplayedColumns = l;
    Collections.sort(this.listDisplayedColumns);
    Iterator it = this.listDisplayedColumns.iterator();
    while (it.hasNext()) {
        GanttTreeTable.DisplayedColumn dc = ((GanttTreeTable.DisplayedColumn) (it.next()));
        String id = dc.getID();
        String name = getNameForId(id);
        if (dc.displayed)
            displayColumn(name);
        else
            hideColumn(name);

    } 
}

private void hideColumn(String name) {
    Iterator it = mapTableColumnColumnKeeper.keySet().iterator();
    while (it.hasNext()) {
        TableColumn c = ((TableColumn) (it.next()));
        String n = ((String) (c.getHeaderValue()));
        if (n.equals(name)) {
            GanttTreeTable.ColumnKeeper ck = ((GanttTreeTable.ColumnKeeper) (mapTableColumnColumnKeeper.get(c)));
            if (ck.isShown)
                ck.hide();

            break;
        }
    } 
    // createPopupMenu();
}

/**
 * Delete permanently a custom column.
 *
 * @param name
 * 		Name of the column to delete.
 */
public void deleteCustomColumn(CustomColumn column) {
    final String name = column.getName();
    // the column has to be displayed to be removed.
    this.displayColumn(name);
    deleteColumnFromUI(name);
    // Every tasks
    TaskContainmentHierarchyFacade tchf = Mediator.getGanttProjectSingleton().getTaskManager().getTaskHierarchy();
    tchf.getRootTask().getCustomValues().removeCustomColumn(name);
    removeCustomColumnToAllNestedTask(tchf, tchf.getRootTask(), name);
    Mediator.getGanttProjectSingleton().setAskForSave(true);
}

