public boolean isDisplayed(String name) {
    Iterator it = this.iterator();
    while (it.hasNext()) {
        GanttTreeTable.DisplayedColumn dc = ((GanttTreeTable.DisplayedColumn) (it.next()));
        if (getNameForId(dc.getID()).equals(name))
            return dc.isDisplayed();

    } 
    return false;
}

