    for (int i = 0; i < 7; i++) {
        start.add((-1));
        finish.add((-1));
        if (((date.equals(start.getTime())) || (date.equals(finish.getTime()))) || ((date.before(finish.getTime())) && (date.after(start.getTime()))))
