    if (areUnitsAccepted) {
        // if (myRedlineOption.isChecked()
        // && myCurrentFrame.getUnitStart(myCurrentUnit, unitIndex)
        // .compareTo(myToday) <= 0
        // && myCurrentFrame.getUnitFinish(myCurrentUnit, unitIndex)
