    Component rootComponent = getComponent();
    getUIFacade().showDialog(rootComponent, new Action[]{ new OkAction() {
        public void actionPerformed(ActionEvent e) {
            myOptionGroup.commit();
            myDate.commit();
            GanttDialogCustomColumn.this.ok();
