        JButton no = new JButton(language.getText("no"));
        p.add(no);
        no.addActionListener(new ActionListener() {
            public void actionPerformed(ActionEvent evt) {
                setVisible(false);
                dispose();
                res = GanttDialogInfo.NO;
            }
        });
