            p.add(cancel);
            cancel.addActionListener(new ActionListener() {
                public void actionPerformed(ActionEvent evt) {
                    setVisible(false);
                    dispose();
                    res = GanttDialogInfo.CANCEL;
                }
            });
        }
    }
    getContentPane().add(p, BorderLayout.SOUTH);
