}

private boolean canBeProjectTask(Task testedTask, TaskContainmentHierarchyFacade taskHierarchy) {
    Task[] nestedTasks = taskHierarchy.getNestedTasks(testedTask);
    if ((nestedTasks.length) == 0) {
        return false;
    }
    for (Task parent = taskHierarchy.getContainer(testedTask); parent != null; parent = taskHierarchy.getContainer(parent)) {
        if (parent.isProjectTask()) {
            return false;
        }
    }
    for (int i = 0; i < (nestedTasks.length); i++) {
        if (isProjectTaskOrContainsProjectTask(nestedTasks[i])) {
            return false;
        }
    }
    return true;
}

private boolean canBeProjectTask(Task testedTask, TaskContainmentHierarchyFacade taskHierarchy) {
    Task[] nestedTasks = taskHierarchy.getNestedTasks(testedTask);
    if ((nestedTasks.length) == 0) {
        return false;
    }
    for (Task parent = taskHierarchy.getContainer(testedTask); parent != null; parent = taskHierarchy.getContainer(parent)) {
        if (parent.isProjectTask()) {
            return false;
        }
    }
    for (int i = 0; i < (nestedTasks.length); i++) {
        if (isProjectTaskOrContainsProjectTask(nestedTasks[i])) {
            return false;
        }
    }
    return true;
}

private boolean isProjectTaskOrContainsProjectTask(Task task) {
    if (task.isProjectTask()) {
        return true;
    }
    boolean result = false;
    Task[] nestedTasks = task.getNestedTasks();
    for (int i = 0; i < (nestedTasks.length); i++) {
        if (isProjectTaskOrContainsProjectTask(nestedTasks[i])) {
            result = true;
            break;
        }
    }
    return result;
}

/**
 * The constructor
 */
public GanttURLChooser(Frame parent, boolean opening, String currentURL, String currentUser, String currentPass) {
    super(parent, (opening ? GanttProject.correctLabel(GanttLanguage.getInstance().getText("openFromServer")) : GanttProject.correctLabel(GanttLanguage.getInstance().getText("saveToServer"))), true);
    JPanel inputPanel = new JPanel(new BorderLayout());
    Box serverBox = Box.createVerticalBox();
    serverBox.add(new JLabel(language.getText("fileFromServer")));
    String sDefaultURL = "http://ganttproject.sourceforge.net/tmp/testGantt.xml";
    urlField = new JTextField((null != currentURL ? currentURL : sDefaultURL));
    serverBox.add(urlField);
    inputPanel.add(serverBox, BorderLayout.NORTH);
    Box hb = Box.createHorizontalBox();// horizontal box

    Box ivbw = Box.createVerticalBox();// inner vertical box west

    Box ivbe = Box.createVerticalBox();// inner vertical box east

    ivbw.add(new JLabel(language.getText("userName")));
    userNameField = new JTextField(currentUser);
    ivbw.add(userNameField);
    ivbe.add(new JLabel(language.getText("password")));
    passwordField = new JPasswordField(currentPass);
    ivbe.add(passwordField);
    hb.add(ivbw);
    hb.add(ivbe);
    inputPanel.add(hb, BorderLayout.SOUTH);
    JPanel centerPanel = new JPanel(new FlowLayout());
    centerPanel.add(inputPanel, BorderLayout.CENTER);
    getContentPane().add(centerPanel, BorderLayout.CENTER);
    JPanel southPanel = new JPanel();
    JButton ok = new JButton(language.getText("ok"));
    getRootPane().setDefaultButton(ok);
