    RoleSet[] roleSets = roleManager.getRoleSets();
    for (int i = 0; i < (roleSets.length); i++) {
        RoleSet next = roleSets[i];
        if (next.isEnabled()) {
            addAttribute("roleset-name", next.getName(), attrs);
            emptyElement("roles", attrs, handler);
