public boolean equals(Object obj) {
    if (obj instanceof ShapePaint) {
        ShapePaint paint = ((ShapePaint) (obj));
        if ((array.length) != (paint.array.length))
            return false;

        for (int i = 0; i < (array.length); i++)
            if ((array[i]) != (paint.array[i]))
