public String getIdFromName(String name) {
    String id = null;
    Iterator it = mapIdCustomColum.values().iterator();
    while (it.hasNext()) {
        CustomColumn cc = ((CustomColumn) (it.next()));
        if (cc.getName().equals(name)) {
            id = cc.getId();
            break;
        }
    } 
    return id;
