private static boolean isIntervalNonWorking(GanttCalendar d1, GanttCalendar d2, GPCalendar cal) {
    while ((d1.before(d2)) && ((cal.getWeekDayType(d1.getDayWeek())) != (GPCalendar.DayType.WORKING)))
        d1.add(1);

    return d1.equals(d2);
}

