        long ovID = Long.valueOf(input);
        if (ovDAO.checkOfficeVisitExists(ovID, pid))
            return ovID;
        else
            throw new iTrustException((((("Office Visit " + ovID) + " with Patient MID ") + (pid)) + " does not exist"));

