            return false;

    } else
        if (!(HCPAddr.equals(other.HCPAddr)))
            return false;


    if ((HCPName) == null) {
        if ((other.HCPName) != null)
            return false;

    } else
        if (!(HCPName.equals(other.HCPName)))
            return false;


    if ((HCPSpecialty) == null) {
        if ((other.HCPSpecialty) != null)
            return false;

    } else
        if (!(HCPSpecialty.equals(other.HCPSpecialty)))
            return false;


    if ((OVDate) == null) {
        if ((other.OVDate) != null)
            return false;

    } else
        if (!(OVDate.equals(other.OVDate)))
            return false;


    if ((designated) != (other.designated))
        return false;

    if ((hcpMID) != (other.hcpMID))
        return false;

    return true;
}

