 * @throws iTrustException
 * 		
 * @throws DBException
 * 		
 */
public String getName(long mid) throws DBException, iTrustException {
    Connection conn = null;
    PreparedStatement pstmt = null;
    try {
        conn = factory.getConnection();
        pstmt = conn.prepareStatement("SELECT firstName, lastName FROM personnel WHERE MID=?");
        pstmt.setLong(1, mid);
        ResultSet rs;
        rs = pstmt.executeQuery();
        if (rs.next()) {
            return ((rs.getString("firstName")) + " ") + (rs.getString("lastName"));
        } else {
            throw new iTrustException("User does not exist");
        }
    } catch (SQLException e) {
        e.printStackTrace();
        throw new DBException(e);
    } finally {
        DBUtil.closeConnection(conn, pstmt);
    }
}

public long getNextID(Role role) throws DBException, iTrustException {
    Connection conn = null;
    PreparedStatement ps = null;
    // long newID = 9000000000L;
    long minID = (role.getMidFirstDigit()) * 1000000000L;
    minID = (minID == 0) ? 1 : minID;// Do not use 0 as an ID.

    long maxID = minID + 999999998L;
    long nextID = minID;
    try {
        conn = factory.getConnection();
        ps = conn.prepareStatement("SELECT MAX(users.mid) FROM users WHERE mid BETWEEN ? AND ?");
        ps.setLong(1, minID);
        ps.setLong(2, maxID);
        ResultSet rs = ps.executeQuery();
        if (rs.next()) {
            nextID = (rs.getLong(1)) + 1;
            if (nextID < minID) {
                nextID = minID;
            }
        }
        return nextID;
    } catch (SQLException e) {
        e.printStackTrace();
        throw new DBException(e);
    } finally {
        DBUtil.closeConnection(conn, ps);
    }
}

/**
 * Adds an empty personnel, and returns the MID.
 *
 * @return A long indicating the new MID.
 * @param role
 * 		A {@link Role} enum indicating the personnels specific role.
 * @throws DBException
 * 		
 * @throws iTrustException
 * 		
 */
public long addEmptyPersonnel(Role role) throws DBException, iTrustException {
    Connection conn = null;
    PreparedStatement ps = null;
    long nextID = getNextID(role);
    try {
        conn = factory.getConnection();
        ps = conn.prepareStatement("INSERT INTO personnel(MID, Role) VALUES(?,?)");
        ps.setString(1, ("" + nextID));
        ps.setString(2, role.name());
        ps.executeUpdate();
        return nextID;
    } catch (SQLException e) {
        System.out.println(e.getMessage());
        e.printStackTrace();
        throw new DBException(e);
    } finally {
        DBUtil.closeConnection(conn, ps);
    }
}

