        errorList.addIfNotNull(checkFormat("Diastolic Blood Pressure", ("" + (m.getDiastolicBloodPressure())), ValidationFormat.DIASTOLIC_BLOOD_PRESSURE, true));

    if (!(((m.getGlucoseLevel()) == 0) || ((m.getGlucoseLevel()) == (-1))))
        errorList.addIfNotNull(checkFormat("Glucose Level", ("" + (m.getGlucoseLevel())), ValidationFormat.GLUCOSE_LEVEL, true));

    if (!(((m.getPedometerReading()) == 0) || ((m.getPedometerReading()) == (-1))))
        errorList.addIfNotNull(checkFormat("Pedometer Reading", ("" + (m.getPedometerReading())), ValidationFormat.PEDOMETER_READING, true));

    if (!(((m.getHeight()) == 0) || ((m.getHeight()) == (-1))))
        errorList.addIfNotNull(checkFormat("Height", ("" + (m.getHeight())), ValidationFormat.HEIGHT, true));

    if (!(((m.getWeight()) == 0) || ((m.getWeight()) == (-1))))
        errorList.addIfNotNull(checkFormat("Weight", ("" + (m.getWeight())), ValidationFormat.WEIGHT, true));

    if (errorList.hasErrors()) {
        throw new FormValidationException(errorList);
    }
